import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/api';
import toast from 'react-hot-toast';

import { Search, Filter, Brain, Zap, Award, Clock, Users, Star } from 'lucide-react';

const Quizzes = () => {
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [selectedDifficulty, setSelectedDifficulty] = useState('all');
  const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);


  useEffect(() => {
    loadQuizzes();
  }, [searchTerm, selectedCategory, selectedDifficulty]);

  const loadQuizzes = async () => {
    try {
      setLoading(true);
      const params = {};
      if (searchTerm && searchTerm.trim()) params.search = searchTerm;
      if (selectedCategory && selectedCategory !== 'all') params.category = selectedCategory;
      if (selectedDifficulty && selectedDifficulty !== 'all') params.difficulty = selectedDifficulty;
      params.limit = 20;
      
      const response = await apiService.getQuizzes(params);
      setQuizzes(response.data.quizzes || []);
    } catch (error) {
      console.error('âŒ Error loading quizzes:', error);
      toast.error('Failed to load quizzes');
      setQuizzes([]);
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    { id: 'all', name: 'All Categories', icon: Brain },
    { id: 'math', name: 'Mathematics', icon: Brain },
    { id: 'science', name: 'Science', icon: Zap },
    { id: 'history', name: 'History', icon: Award },
    { id: 'language', name: 'Language', icon: Brain },
    { id: 'geography', name: 'Geography', icon: Brain },
    { id: 'art', name: 'Art', icon: Award }
  ];

  const difficulties = [
    { id: 'all', name: 'All Levels' },
    { id: 'beginner', name: 'Beginner' },
    { id: 'intermediate', name: 'Intermediate' },
    { id: 'advanced', name: 'Advanced' }
  ];

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 'beginner': return 'bg-green-100 text-green-800';
      case 'intermediate': return 'bg-yellow-100 text-yellow-800';
      case 'advanced': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getCategoryIcon = (category) => {
    const categoryObj = categories.find(c => c.id === category);
    return categoryObj ? categoryObj.icon : Brain;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="animate-fade-in">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">Quizzes</h1>
            <p className="text-gray-600">Test your knowledge and earn points</p>
          </div>
          <div className="flex space-x-2">
            <button
              onClick={loadQuizzes}
              className="btn-secondary flex items-center space-x-2"
            >
              <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
              </svg>
              <span>Refresh</span>
            </button>
            <button
              onClick={() => {
                console.log('ðŸ§ª Testing with dummy data...');
                setQuizzes([
                  {
                    _id: 'test1',
                    title: 'Test Quiz 1',
                    description: 'This is a test quiz',
                    category: 'math',
                    difficulty: 'beginner',
                    timeLimit: 10,
                    totalPoints: 100,
                    questions: [{}, {}, {}]
                  }
                ]);
              }}
              className="btn-primary flex items-center space-x-2"
            >
              <span>Test</span>
            </button>
          </div>
        </div>
      </div>

      {/* Filters */}
      <div className="animate-fade-in card">
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {/* Search */}
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search quizzes..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* Category Filter */}
            <div>
              <select
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {categories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
            </div>

            {/* Difficulty Filter */}
            <div>
              <select
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                value={selectedDifficulty}
                onChange={(e) => setSelectedDifficulty(e.target.value)}
              >
                {difficulties.map((difficulty) => (
                  <option key={difficulty.id} value={difficulty.id}>
                    {difficulty.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>
      </div>

      {/* Quizzes Grid */}
      <div className="animate-fade-in">
        {quizzes.length === 0 ? (
          <div className="text-center py-12">
            <Brain className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No quizzes found</h3>
            <p className="text-gray-500">Try adjusting your filters or check back later for new quizzes.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {quizzes.map((quiz) => {
              const CategoryIcon = getCategoryIcon(quiz.category);
              
              return (
                <div
                  key={quiz._id}
                  className="card hover:shadow-lg transition-all duration-200 group"
                >
                  <div className="p-6">
                    {/* Quiz Header */}
                    <div className="flex items-start justify-between mb-4">
                      <div className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-primary-100 rounded-lg flex items-center justify-center">
                          <CategoryIcon className="w-5 h-5 text-primary-600" />
                        </div>
                        <div>
                          <span className="text-sm text-gray-500 capitalize">{quiz.category}</span>
                          <div className="flex items-center space-x-2 mt-1">
                            <Star className="w-4 h-4 text-yellow-400 fill-current" />
                            <span className="text-sm font-medium">4.8</span>
                          </div>
                        </div>
                      </div>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getDifficultyColor(quiz.difficulty)}`}>
                        {quiz.difficulty}
                      </span>
                    </div>

                    {/* Quiz Content */}
                    <h3 className="text-lg font-bold text-gray-900 mb-2 line-clamp-2">{quiz.title}</h3>
                    <p className="text-gray-600 mb-4 line-clamp-2">{quiz.description}</p>

                    {/* Quiz Stats */}
                    <div className="grid grid-cols-3 gap-4 mb-4 text-center">
                      <div>
                        <div className="flex items-center justify-center mb-1">
                          <Clock className="w-4 h-4 text-gray-400" />
                        </div>
                        <div className="text-sm font-medium text-gray-900">{quiz.timeLimit}m</div>
                        <div className="text-xs text-gray-500">Duration</div>
                      </div>
                      <div>
                        <div className="flex items-center justify-center mb-1">
                          <Brain className="w-4 h-4 text-gray-400" />
                        </div>
                        <div className="text-sm font-medium text-gray-900">{quiz.questions?.length || 0}</div>
                        <div className="text-xs text-gray-500">Questions</div>
                      </div>
                      <div>
                        <div className="flex items-center justify-center mb-1">
                          <Users className="w-4 h-4 text-gray-400" />
                        </div>
                        <div className="text-sm font-medium text-gray-900">{quiz.totalPoints || 0}</div>
                        <div className="text-xs text-gray-500">Points</div>
                      </div>
                    </div>

                    {/* Tags */}
                    {quiz.tags && quiz.tags.length > 0 && (
                      <div className="flex flex-wrap gap-2 mb-4">
                        {quiz.tags.slice(0, 3).map((tag, index) => (
                          <span key={index} className="px-2 py-1 bg-gray-100 text-gray-600 text-xs rounded-full">
                            #{tag}
                          </span>
                        ))}
                      </div>
                    )}

                    {/* Action Button */}
                    <button
                      onClick={() => window.location.href = `/app/quiz/${quiz._id}`}
                      className="w-full btn-primary flex items-center justify-center space-x-2 group-hover:shadow-lg transition-all duration-200"
                    >
                      <Brain className="w-4 h-4" />
                      <span>Start Quiz</span>
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default Quizzes;
