import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/api';
import socketService from '../../services/socketService';
import toast from 'react-hot-toast';

import {
  Play,
  Clock,
  Users,
  Target,
  TrendingUp,
  Star,
  Brain,
  Zap,
  Award,
  ChevronRight,
  Trophy,
  BookOpen
} from 'lucide-react';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState([]);
  const [quizzes, setQuizzes] = useState([]);
  const [achievements, setAchievements] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDashboardData();
    setupSocketListeners();
    
    return () => {
      socketService.cleanup();
    };
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // Load user progress
      const progressResponse = await apiService.getUserProgress();
      const progressData = progressResponse.data;
      
      // Load quizzes
      const quizzesResponse = await apiService.getQuizzes({ limit: 4 });
      setQuizzes(quizzesResponse.data.quizzes);
      
      // Load achievements
      const achievementsResponse = await apiService.getUserAchievements();
      setAchievements(achievementsResponse.data.unlocked.slice(0, 3));
      
      // Set stats
      setStats([
        {
          title: 'Quizzes Completed',
          value: progressData.user.stats.totalQuizzes.toString(),
          change: `+${progressData.progress.totalAttempts} total`,
          icon: Target,
          color: 'text-primary-600',
          bgColor: 'bg-primary-100'
        },
        {
          title: 'Total Points',
          value: progressData.user.stats.totalPoints.toLocaleString(),
          change: `+${progressData.progress.totalAttempts * 10} earned`,
          icon: Star,
          color: 'text-warning-600',
          bgColor: 'bg-warning-100'
        },
        {
          title: 'Current Streak',
          value: `${progressData.user.stats.currentStreak} days`,
          change: 'Keep it up!',
          icon: Zap,
          color: 'text-success-600',
          bgColor: 'bg-success-100'
        },
        {
          title: 'Level',
          value: `${progressData.user.level}`,
          change: `${progressData.user.experience} XP`,
          icon: TrendingUp,
          color: 'text-info-600',
          bgColor: 'bg-info-100'
        }
      ]);
      
    } catch (error) {
      console.error('Error loading dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const setupSocketListeners = () => {
    // Connect to socket
    socketService.connect();
    
    // Join user room
    if (user?.id) {
      socketService.joinUserRoom(user.id);
    }
    
    // Join leaderboard room
    socketService.joinLeaderboard();
    
    // Listen for notifications
    socketService.onQuizCompleted((data) => {
      toast.success(`Quiz completed! Score: ${data.score}%`);
      loadDashboardData(); // Refresh data
    });
    
    socketService.onAchievementUnlocked((data) => {
      toast.success(`ðŸŽ‰ Achievement unlocked: ${data.achievement.name}!`);
      loadDashboardData(); // Refresh data
    });
    
    socketService.onLevelUp((data) => {
      toast.success(`ðŸŽ‰ Level up! You're now level ${data.newLevel}!`);
      loadDashboardData(); // Refresh data
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="animate-fade-in bg-gradient-to-r from-primary-600 to-secondary-600 rounded-2xl p-8 text-white shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold mb-2">
              Welcome back, {user?.firstName || 'Learner'}! ðŸŽ¯
            </h1>
            <p className="text-primary-100 text-lg">
              Ready to continue your learning journey? You're doing amazing!
            </p>
          </div>
          <div className="hidden md:block">
            <div className="w-20 h-20 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
              <Brain className="w-10 h-10 text-white" />
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => (
          <div className="animate-fade-in card hover:shadow-xl" key={stat.title}>
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600 mb-1">{stat.title}</p>
                <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
                <p className="text-xs text-green-600 mt-1">{stat.change}</p>
              </div>
              <div className={`${stat.bgColor} p-3 rounded-xl`}>
                <stat.icon className={`w-6 h-6 ${stat.color}`} />
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Main Content */}
        <div className="lg:col-span-2 space-y-8">
          {/* Featured Quizzes */}
          <div className="animate-fade-in card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">Featured Quizzes</h2>
              <a
                href="/app/quizzes"
                className="text-primary-600 hover:text-primary-700 font-medium flex items-center space-x-1"
              >
                <span>View All</span>
                <ChevronRight className="w-4 h-4" />
              </a>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {quizzes.map((quiz, index) => (
                <div className="animate-fade-in" key={quiz._id}>
                  <div className="card hover:shadow-lg transition-all duration-200">
                    <div className="p-6">
                      <div className="flex items-center justify-between mb-2">
                        <span className="text-sm text-gray-500 capitalize">{quiz.category}</span>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          quiz.difficulty === 'beginner' ? 'bg-green-100 text-green-800' :
                          quiz.difficulty === 'intermediate' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {quiz.difficulty}
                        </span>
                      </div>
                      <h3 className="text-lg font-bold text-gray-900 mb-2">{quiz.title}</h3>
                      <p className="text-gray-600 mb-4 line-clamp-2">{quiz.description}</p>
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4 text-sm text-gray-500">
                          <div className="flex items-center space-x-1">
                            <Clock className="w-4 h-4" />
                            <span>{quiz.timeLimit}m</span>
                          </div>
                          <div className="flex items-center space-x-1">
                            <Brain className="w-4 h-4" />
                            <span>{quiz.questions?.length || 0} Q</span>
                          </div>
                        </div>
                        <Link
                          to={`/app/quiz/${quiz._id}`}
                          className="btn-primary text-sm px-4 py-2 flex items-center space-x-2 hover:shadow-md transition-all duration-200"
                        >
                          <Play className="w-4 h-4" />
                          <span>Start Quiz</span>
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Quick Actions */}
          <div className="animate-fade-in card">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <button
                onClick={() => console.log('Starting daily challenge...')}
                className="w-full btn-primary flex items-center justify-center space-x-2"
              >
                <Play className="w-4 h-4" />
                <span>Start Daily Challenge</span>
              </button>
              <button
                onClick={() => console.log('Joining study group...')}
                className="w-full btn-secondary flex items-center justify-center space-x-2"
              >
                <Users className="w-4 h-4" />
                <span>Join Study Group</span>
              </button>
              <button
                onClick={() => console.log('Starting practice mode...')}
                className="w-full bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold py-3 px-6 rounded-xl transition-all duration-200 flex items-center justify-center space-x-2"
              >
                <Clock className="w-4 h-4" />
                <span>Practice Mode</span>
              </button>
            </div>
          </div>

          {/* Recent Achievements */}
          <div className="animate-fade-in card">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Recent Achievements</h3>
            <div className="space-y-3">
              {achievements.length > 0 ? (
                achievements.map((achievement) => (
                  <div key={achievement._id} className="flex items-center space-x-3 p-3 bg-gradient-to-r from-warning-50 to-warning-100 rounded-lg">
                    <div className="w-10 h-10 bg-warning-500 rounded-full flex items-center justify-center">
                      <span className="text-white text-lg">{achievement.icon}</span>
                    </div>
                    <div>
                      <p className="font-semibold text-gray-900">{achievement.name}</p>
                      <p className="text-sm text-gray-600">{achievement.description}</p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-gray-500 text-center py-4">No achievements yet. Complete quizzes to unlock them!</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
