import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

import { 
  Play, 
  Clock, 
  Users, 
  Star, 
  Brain,
  CheckCircle,
  ArrowLeft,
  ArrowRight,
  Trophy,
  Target
} from 'lucide-react';
import { mockQuizzes, mockQuizQuestions } from '../../data/mockData';
import toast from 'react-hot-toast';
import { ConfettiButton } from '../../components/lightswind/confetti-button.tsx';

const QuizDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [timeLeft, setTimeLeft] = useState(15 * 60); // 15 minutes in seconds
  const [isQuizStarted, setIsQuizStarted] = useState(false);
  const [isQuizCompleted, setIsQuizCompleted] = useState(false);

  const quiz = mockQuizzes.find(q => q.id === id) || mockQuizzes[0];
  const questions = mockQuizQuestions;

  // Timer effect
  React.useEffect(() => {
    if (isQuizStarted && timeLeft > 0 && !isQuizCompleted) {
      const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
      return () => clearTimeout(timer);
    } else if (timeLeft === 0 && !isQuizCompleted) {
      handleSubmitQuiz();
    }
  }, [timeLeft, isQuizStarted, isQuizCompleted]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleAnswerSelect = (questionIndex, answerIndex) => {
    setAnswers({
      ...answers,
      [questionIndex]: answerIndex
    });
  };

  const handleNextQuestion = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
  };

  const handleSubmitQuiz = () => {
    setIsQuizCompleted(true);
    const correctAnswers = questions.filter((q, index) => answers[index] === q.correctAnswer).length;
    const score = Math.round((correctAnswers / questions.length) * 100);
    
    toast.success(`Quiz completed! Your score: ${score}%`);
  };

  const startQuiz = () => {
    setIsQuizStarted(true);
    setTimeLeft(quiz.timeLimit * 60);
  };

  if (isQuizCompleted) {
    const correctAnswers = questions.filter((q, index) => answers[index] === q.correctAnswer).length;
    const score = Math.round((correctAnswers / questions.length) * 100);

    return (
      <div className="max-w-4xl mx-auto">
        <div className="animate-fade-in card text-center">
          <div className="w-20 h-20 bg-gradient-to-r from-success-500 to-success-600 rounded-full flex items-center justify-center mx-auto mb-6">
            <CheckCircle className="w-10 h-10 text-white" />
          </div>
          
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Quiz Completed!</h1>
          <p className="text-xl text-gray-600 mb-8">Great job on the {quiz.title}</p>
          
          <div className="grid grid-cols-3 gap-6 mb-8">
            <div className="text-center">
              <div className="text-3xl font-bold text-primary-600">{score}%</div>
              <div className="text-gray-600">Score</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-success-600">{correctAnswers}</div>
              <div className="text-gray-600">Correct</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-warning-600">{quiz.points}</div>
              <div className="text-gray-600">Points Earned</div>
            </div>
          </div>

          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <ConfettiButton
              onClick={() => navigate('/app/quizzes')}
              variant="gradient"
              size="lg"
              animation="expand"
              icon={<ArrowLeft className="w-5 h-5" />}
              iconPosition="left"
              confettiOptions={{
                particleCount: 150,
                spread: 60,
                colors: ['#3b82f6', '#8b5cf6', '#06b6d4']
              }}
            >
              Back to Quizzes
            </ConfettiButton>
            <ConfettiButton
              onClick={() => navigate('/app/dashboard')}
              variant="outline"
              size="lg"
              animation="bounce"
              icon={<Trophy className="w-5 h-5" />}
              iconPosition="left"
              confettiOptions={{
                particleCount: 200,
                spread: 70,
                colors: ['#f59e0b', '#ef4444', '#10b981']
              }}
            >
              Go to Dashboard
            </ConfettiButton>
          </div>
        </div>
      </div>
    );
  }

  if (!isQuizStarted) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="animate-fade-in card">
          <div className="flex items-center space-x-4 mb-6">
            <button
              onClick={() => navigate('/app/quizzes')}
              className="p-2 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <ArrowLeft className="w-5 h-5" />
            </button>
            <h1 className="text-2xl font-bold text-gray-900">{quiz.title}</h1>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Quiz Details</h2>
              <div className="space-y-4">
                <div className="flex items-center space-x-3">
                  <Clock className="w-5 h-5 text-gray-400" />
                  <span className="text-gray-600">Time Limit: {quiz.timeLimit} minutes</span>
                </div>
                <div className="flex items-center space-x-3">
                  <Brain className="w-5 h-5 text-gray-400" />
                  <span className="text-gray-600">Questions: {questions.length}</span>
                </div>
                <div className="flex items-center space-x-3">
                  <Star className="w-5 h-5 text-gray-400" />
                  <span className="text-gray-600">Points: {quiz.points}</span>
                </div>
                <div className="flex items-center space-x-3">
                  <Users className="w-5 h-5 text-gray-400" />
                  <span className="text-gray-600">Participants: {quiz.participants.toLocaleString()}</span>
                </div>
              </div>
            </div>

            <div>
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Instructions</h2>
              <ul className="space-y-2 text-gray-600">
                <li>• Read each question carefully</li>
                <li>• Select the best answer</li>
                <li>• You can navigate between questions</li>
                <li>• Submit when you're ready</li>
                <li>• Time will be tracked automatically</li>
              </ul>
            </div>
          </div>

          <div className="mt-8 text-center">
            <ConfettiButton
              onClick={startQuiz}
              variant="gradient"
              size="xl"
              animation="expand"
              icon={<Play className="w-5 h-5" />}
              iconPosition="left"
              className="mx-auto"
              confettiOptions={{
                particleCount: 200,
                spread: 70,
                startVelocity: 35,
                colors: ['#3b82f6', '#8b5cf6', '#06b6d4', '#10b981', '#f59e0b']
              }}
            >
              Start Quiz
            </ConfettiButton>
          </div>
        </div>
      </div>
    );
  }

  const currentQ = questions[currentQuestion];

  return (
    <div className="max-w-4xl mx-auto">
      {/* Quiz Header */}
      <div className="animate-fade-in card mb-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-xl font-bold text-gray-900">{quiz.title}</h1>
          <div className="flex items-center space-x-4">
            <div className="text-sm text-gray-600">
              Question {currentQuestion + 1} of {questions.length}
            </div>
            <div className="flex items-center space-x-2 text-warning-600">
              <Clock className="w-4 h-4" />
              <span className="font-mono">{formatTime(timeLeft)}</span>
            </div>
          </div>
        </div>

        {/* Progress Bar */}
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div
            className="bg-gradient-to-r from-primary-500 to-secondary-500 h-2 rounded-full transition-all duration-300"
            style={{ width: `${((currentQuestion + 1) / questions.length) * 100}%` }}
          ></div>
        </div>
      </div>

      {/* Question */}
      <div className="animate-fade-in"div
        key={currentQuestion}
        initial={{ opacity: 0, x: 20 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.3 }}
        className="card"
      >
        <h2 className="text-lg font-semibold text-gray-900 mb-6">
          {currentQ.question}
        </h2>

        <div className="space-y-3 mb-8">
          {currentQ.options.map((option, index) => (
            <button
              key={index}
              onClick={() => handleAnswerSelect(currentQuestion, index)}
              className={`w-full text-left p-4 rounded-lg border-2 transition-all duration-200 ${
                answers[currentQuestion] === index
                  ? 'border-primary-500 bg-primary-50 text-primary-700'
                  : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
              }`}
            >
              {option}
            </button>
          ))}
        </div>

        {/* Navigation */}
        <div className="flex items-center justify-between">
          <button
            onClick={handlePreviousQuestion}
            disabled={currentQuestion === 0}
            className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-gray-900 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <ArrowLeft className="w-4 h-4" />
            <span>Previous</span>
          </button>

          <div className="flex space-x-2">
            {questions.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentQuestion(index)}
                className={`w-8 h-8 rounded-full text-sm font-medium transition-colors ${
                  index === currentQuestion
                    ? 'bg-primary-500 text-white'
                    : answers[index] !== undefined
                    ? 'bg-success-100 text-success-700'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }`}
              >
                {index + 1}
              </button>
            ))}
          </div>

          {currentQuestion === questions.length - 1 ? (
            <button
              onClick={handleSubmitQuiz}
              className="btn-primary flex items-center space-x-2"
            >
              <CheckCircle className="w-4 h-4" />
              <span>Submit Quiz</span>
            </button>
          ) : (
            <button
              onClick={handleNextQuestion}
              className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-gray-900"
            >
              <span>Next</span>
              <ArrowRight className="w-4 h-4" />
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default QuizDetail;
