import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/api';
import socketService from '../../services/socketService';
import toast from 'react-hot-toast';

import {
  Play,
  Clock,
  Users,
  Target,
  TrendingUp,
  Star,
  Brain,
  Zap,
  Award,
  ChevronRight,
  Trophy,
  BookOpen
} from 'lucide-react';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState([]);
  const [quizzes, setQuizzes] = useState([]);
  const [achievements, setAchievements] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDashboardData();
    setupSocketListeners();
    
    return () => {
      // Cleanup socket listeners
      socketService.removeAllListeners('quiz-completed');
      socketService.removeAllListeners('achievement-unlocked');
      socketService.removeAllListeners('level-up');
    };
  }, []);

  // Refresh data when component becomes visible
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden) {
        loadDashboardData();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => document.removeEventListener('visibilitychange', handleVisibilityChange);
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // Load user progress, stats, quizzes, and achievements in parallel
      const [progressResponse, statsResponse, quizzesResponse, achievementsResponse] = await Promise.all([
        apiService.getUserProgress(),
        apiService.getUserStats(),
        apiService.getQuizzes({ limit: 6 }),
        apiService.getUserAchievements()
      ]);

      // Set stats data
      const progressData = progressResponse.data;
      const statsData = statsResponse.data;
      
      setStats([
        {
          id: 'quizzes-completed',
          title: 'Quizzes Completed',
          value: progressData?.totalAttempts || 0,
          change: '+12%',
          trend: 'up',
          icon: Brain,
          color: 'from-blue-500 to-blue-600'
        },
        {
          id: 'total-points',
          title: 'Total Points',
          value: progressData?.totalPoints || 0,
          change: '+8%',
          trend: 'up',
          icon: Trophy,
          color: 'from-purple-500 to-purple-600'
        },
        {
          id: 'current-streak',
          title: 'Current Streak',
          value: progressData?.currentStreak || 0,
          change: '+3',
          trend: 'up',
          icon: Zap,
          color: 'from-green-500 to-green-600'
        },
        {
          id: 'level',
          title: 'Level',
          value: progressData?.level || 1,
          change: `+${progressData?.experienceGained || 0} XP`,
          trend: 'up',
          icon: Star,
          color: 'from-orange-500 to-orange-600',
          progress: progressData?.xpProgress || 0
        }
      ]);

      setQuizzes(quizzesResponse.data.quizzes || []);
      setAchievements(achievementsResponse.data || []);
      
    } catch (error) {
      console.error('Error loading dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const setupSocketListeners = () => {
    // Listen for notifications
    socketService.onQuizCompleted((data) => {
      toast.success(`Quiz completed! Score: ${data.score}%`);
      loadDashboardData(); // Refresh data
    });
    
    socketService.onAchievementUnlocked((data) => {
      toast.success(`ðŸŽ‰ Achievement unlocked: ${data.achievement.name}!`);
      loadDashboardData(); // Refresh data
    });
    
    socketService.onLevelUp((data) => {
      toast.success(`ðŸŽ‰ Level up! You're now level ${data.newLevel}!`);
      loadDashboardData(); // Refresh data
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="animate-fade-in">
        <div className="bg-gradient-to-r from-primary-600 to-secondary-600 rounded-2xl p-8 text-white">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
            <div>
              <h1 className="text-3xl font-bold mb-2">
                Welcome back, {user?.firstName || 'Learner'}! ðŸ‘‹
              </h1>
              <p className="text-primary-100 text-lg">
                Ready to continue your learning journey? Let's make today productive!
              </p>
            </div>
            <div className="flex items-center space-x-4 mt-4 lg:mt-0">
              <button
                onClick={loadDashboardData}
                disabled={loading}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 disabled:opacity-50 text-white p-2 rounded-lg transition-all duration-200"
                title="Refresh Dashboard"
              >
                <svg 
                  className={`w-5 h-5 ${loading ? 'animate-spin' : ''}`} 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
                </svg>
              </button>
              <Link
                to="/app/quizzes"
                className="bg-white text-primary-600 hover:bg-primary-50 font-semibold py-3 px-6 rounded-xl transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
              >
                Start Learning
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="animate-fade-in">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {stats.map((stat) => (
            <div key={stat.id} className="card group hover:shadow-xl transition-all duration-300">
              <div className="flex items-center justify-between mb-4">
                <div className={`w-12 h-12 bg-gradient-to-r ${stat.color} rounded-xl flex items-center justify-center group-hover:scale-110 transition-transform duration-300`}>
                  <stat.icon className="w-6 h-6 text-white" />
                </div>
                <div className="text-right">
                  <div className="flex items-center space-x-1 text-green-600">
                    <TrendingUp className="w-4 h-4" />
                    <span className="text-sm font-medium">{stat.change}</span>
                  </div>
                </div>
              </div>
              
              <div className="space-y-2">
                <div className="text-2xl font-bold text-gray-900">{stat.value}</div>
                <div className="text-sm text-gray-600">{stat.title}</div>
                
                {stat.progress !== undefined && (
                  <div className="mt-3">
                    <div className="flex justify-between text-xs text-gray-500 mb-1">
                      <span>Progress</span>
                      <span>{stat.progress}%</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className={`bg-gradient-to-r ${stat.color} h-2 rounded-full transition-all duration-500`}
                        style={{ width: `${stat.progress}%` }}
                      ></div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="animate-fade-in">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Recent Quizzes */}
          <div className="card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">Recent Quizzes</h2>
              <Link to="/app/quizzes" className="text-primary-600 hover:text-primary-700 font-medium flex items-center space-x-1">
                <span>View All</span>
                <ChevronRight className="w-4 h-4" />
              </Link>
            </div>
            
            <div className="space-y-4">
              {quizzes.slice(0, 3).map((quiz) => (
                <div key={quiz._id} className="flex items-center justify-between p-4 bg-gray-50 rounded-xl hover:bg-gray-100 transition-colors duration-200">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-primary-100 rounded-lg flex items-center justify-center">
                      <Brain className="w-5 h-5 text-primary-600" />
                    </div>
                    <div>
                      <h3 className="font-medium text-gray-900">{quiz.title}</h3>
                      <p className="text-sm text-gray-500">{quiz.difficulty} â€¢ {quiz.questions?.length || 0} questions</p>
                    </div>
                  </div>
                  <Link
                    to={`/app/quiz/${quiz._id}`}
                    className="bg-primary-600 hover:bg-primary-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors duration-200"
                  >
                    Start
                  </Link>
                </div>
              ))}
            </div>
          </div>

          {/* Recent Achievements */}
          <div className="card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">Recent Achievements</h2>
              <Link to="/app/achievements" className="text-primary-600 hover:text-primary-700 font-medium flex items-center space-x-1">
                <span>View All</span>
                <ChevronRight className="w-4 h-4" />
              </Link>
            </div>
            
            <div className="space-y-4">
              {achievements.slice(0, 3).map((achievement) => (
                <div key={achievement._id} className="flex items-center space-x-3 p-4 bg-gradient-to-r from-yellow-50 to-orange-50 rounded-xl border border-yellow-200">
                  <div className="w-10 h-10 bg-gradient-to-r from-yellow-400 to-orange-500 rounded-lg flex items-center justify-center">
                    <Award className="w-5 h-5 text-white" />
                  </div>
                  <div className="flex-1">
                    <h3 className="font-medium text-gray-900">{achievement.name}</h3>
                    <p className="text-sm text-gray-600">{achievement.description}</p>
                  </div>
                  <div className="text-yellow-600">
                    <Star className="w-5 h-5 fill-current" />
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
