import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/api';
import toast from 'react-hot-toast';

import { 
  User, 
  Mail, 
  Calendar, 
  Award, 
  Target, 
  TrendingUp, 
  Edit3,
  Save,
  X,
  Camera
} from 'lucide-react';
import ProgressChart from '../../components/Charts/ProgressChart';

const Profile = () => {
  const { user, updateProfile } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    preferences: {
      notifications: {
        email: true,
        push: true,
        achievements: true
      },
      difficulty: 'beginner',
      subjects: []
    }
  });
  const [userStats, setUserStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      setEditData({
        firstName: user.firstName || '',
        lastName: user.lastName || '',
        email: user.email || '',
        preferences: user.preferences || {
          notifications: {
            email: true,
            push: true,
            achievements: true
          },
          difficulty: 'beginner',
          subjects: []
        }
      });
    }
    loadUserStats();
  }, [user]);

  const loadUserStats = async () => {
    try {
      setLoading(true);
      const response = await apiService.getUserStats();
      setUserStats(response.data);
    } catch (error) {
      console.error('Error loading user stats:', error);
      toast.error('Failed to load user statistics');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditData({
      firstName: user.firstName || '',
      lastName: user.lastName || '',
      email: user.email || '',
      preferences: user.preferences || {
        notifications: {
          email: true,
          push: true,
          achievements: true
        },
        difficulty: 'beginner',
        subjects: []
      }
    });
  };

  const handleSave = async () => {
    try {
      // Filter out empty strings and prepare clean data
      const cleanData = {
        firstName: editData.firstName.trim() || undefined,
        lastName: editData.lastName.trim() || undefined,
        email: editData.email.trim() || undefined,
        preferences: editData.preferences
      };

      // Remove undefined values
      Object.keys(cleanData).forEach(key => {
        if (cleanData[key] === undefined) {
          delete cleanData[key];
        }
      });

      const result = await updateProfile(cleanData);
      if (result.success) {
        setIsEditing(false);
        toast.success('Profile updated successfully!');
      } else {
        toast.error(result.error || 'Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      const errorMessage = error.message || 'Failed to update profile';
      toast.error(errorMessage);
    }
  };

  const handleInputChange = (field, value) => {
    if (field.includes('.')) {
      const [parent, child] = field.split('.');
      setEditData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setEditData(prev => ({
        ...prev,
        [field]: value
      }));
    }
  };

  const handlePreferenceChange = (category, key, value) => {
    setEditData(prev => ({
      ...prev,
      preferences: {
        ...prev.preferences,
        [category]: {
          ...prev.preferences[category],
          [key]: value
        }
      }
    }));
  };

  const handleSubjectToggle = (subject) => {
    setEditData(prev => ({
      ...prev,
      preferences: {
        ...prev.preferences,
        subjects: prev.preferences.subjects.includes(subject)
          ? prev.preferences.subjects.filter(s => s !== subject)
          : [...prev.preferences.subjects, subject]
      }
    }));
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="animate-fade-in">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 mb-2">Profile</h1>
            <p className="text-gray-600">Manage your account settings and preferences</p>
          </div>
          <button
            onClick={isEditing ? handleSave : handleEdit}
            className={`btn-primary flex items-center space-x-2 ${
              isEditing ? 'bg-green-600 hover:bg-green-700' : ''
            }`}
          >
            {isEditing ? (
              <>
                <Save className="w-4 h-4" />
                <span>Save Changes</span>
              </>
            ) : (
              <>
                <Edit3 className="w-4 h-4" />
                <span>Edit Profile</span>
              </>
            )}
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Profile Information */}
        <div className="lg:col-span-2 space-y-6">
          {/* Basic Info */}
          <div className="card">
            <div className="p-6">
              <h2 className="text-xl font-bold text-gray-900 mb-6">Basic Information</h2>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    First Name
                  </label>
                  {isEditing ? (
                    <input
                      type="text"
                      value={editData.firstName}
                      onChange={(e) => handleInputChange('firstName', e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    />
                  ) : (
                    <p className="text-gray-900">{user?.firstName || 'Not provided'}</p>
                  )}
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Last Name
                  </label>
                  {isEditing ? (
                    <input
                      type="text"
                      value={editData.lastName}
                      onChange={(e) => handleInputChange('lastName', e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    />
                  ) : (
                    <p className="text-gray-900">{user?.lastName || 'Not provided'}</p>
                  )}
                </div>
                
                <div className="md:col-span-2">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Email
                  </label>
                  {isEditing ? (
                    <input
                      type="email"
                      value={editData.email}
                      onChange={(e) => handleInputChange('email', e.target.value)}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    />
                  ) : (
                    <p className="text-gray-900">{user?.email || 'Not provided'}</p>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Preferences */}
          <div className="card">
            <div className="p-6">
              <h2 className="text-xl font-bold text-gray-900 mb-6">Preferences</h2>
              

              {/* Difficulty */}
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Preferred Difficulty</h3>
                <select
                  value={editData.preferences.difficulty}
                  onChange={(e) => handleInputChange('preferences.difficulty', e.target.value)}
                  disabled={!isEditing}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent disabled:bg-gray-100"
                >
                  <option value="beginner">Beginner</option>
                  <option value="intermediate">Intermediate</option>
                  <option value="advanced">Advanced</option>
                </select>
              </div>

              {/* Subjects */}
              <div>
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Interested Subjects</h3>
                <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
                  {['math', 'science', 'history', 'language', 'geography', 'art'].map((subject) => (
                    <label key={subject} className="flex items-center space-x-2">
                      <input
                        type="checkbox"
                        checked={editData.preferences.subjects.includes(subject)}
                        onChange={() => handleSubjectToggle(subject)}
                        disabled={!isEditing}
                        className="w-4 h-4 text-primary-600 border-gray-300 rounded focus:ring-primary-500"
                      />
                      <span className="text-gray-700 capitalize">{subject}</span>
                    </label>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Profile Stats */}
          <div className="card">
            <div className="p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Your Stats</h3>
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Target className="w-5 h-5 text-primary-600" />
                    <span className="text-gray-700">Quizzes Completed</span>
                  </div>
                  <span className="font-bold text-gray-900">{user?.stats?.totalQuizzes || 0}</span>
                </div>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Award className="w-5 h-5 text-yellow-600" />
                    <span className="text-gray-700">Total Points</span>
                  </div>
                  <span className="font-bold text-gray-900">{user?.stats?.totalPoints || 0}</span>
                </div>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <TrendingUp className="w-5 h-5 text-green-600" />
                    <span className="text-gray-700">Current Level</span>
                  </div>
                  <span className="font-bold text-gray-900">{user?.level || 1}</span>
                </div>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <Calendar className="w-5 h-5 text-blue-600" />
                    <span className="text-gray-700">Member Since</span>
                  </div>
                  <span className="font-bold text-gray-900">
                    {user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'N/A'}
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* Progress Chart */}
          <div className="card">
            <div className="p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Progress Overview</h3>
              <ProgressChart />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
