import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import apiService from '../../services/api';
import socketService from '../../services/socketService';
import toast from 'react-hot-toast';

import {
  Play,
  Clock,
  Users,
  Target,
  TrendingUp,
  Star,
  Brain,
  Zap,
  Award,
  ChevronRight,
  Trophy,
  BookOpen
} from 'lucide-react';

const Dashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState([]);
  const [quizzes, setQuizzes] = useState([]);
  const [achievements, setAchievements] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadDashboardData();
    setupSocketListeners();
    
    return () => {
      socketService.cleanup();
    };
  }, []);

  // Refresh dashboard data when component becomes visible (user navigates back)
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (!document.hidden) {
        loadDashboardData();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => document.removeEventListener('visibilitychange', handleVisibilityChange);
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // Load user progress and stats
      const [progressResponse, statsResponse] = await Promise.all([
        apiService.getUserProgress(),
        apiService.getUserStats()
      ]);
      
      const progressData = progressResponse.data;
      const statsData = statsResponse.data;
      
      // Load quizzes and achievements
      const [quizzesResponse, achievementsResponse] = await Promise.all([
        apiService.getQuizzes({ limit: 4 }),
        apiService.getUserAchievements()
      ]);
      
      setQuizzes(quizzesResponse.data.quizzes);
      setAchievements(achievementsResponse.data.unlocked.slice(0, 3));
      
      // Calculate experience to next level
      const currentLevel = progressData.user.stats.level || 1;
      const currentXP = progressData.user.stats.experience || 0;
      const xpNeededForNextLevel = (currentLevel * 1000) - currentXP;
      const xpProgress = ((currentXP % 1000) / 1000) * 100;
      
      // Set dynamic stats with better formatting
      setStats([
        {
          title: 'Quizzes Completed',
          value: (progressData.user.stats.totalQuizzes || 0).toString(),
          change: `${statsData.analytics.overview.totalAttempts || 0} total attempts`,
          icon: Target,
          color: 'text-primary-600',
          bgColor: 'bg-primary-100',
          trend: '+12%'
        },
        {
          title: 'Total Points',
          value: (progressData.user.stats.totalPoints || 0).toLocaleString(),
          change: `${statsData.analytics.overview.totalPoints || 0} earned`,
          icon: Star,
          color: 'text-yellow-600',
          bgColor: 'bg-yellow-100',
          trend: '+8%'
        },
        {
          title: 'Current Streak',
          value: `${progressData.user.stats.currentStreak || 0} days`,
          change: `Longest: ${progressData.user.stats.longestStreak || 0} days`,
          icon: Zap,
          color: 'text-green-600',
          bgColor: 'bg-green-100',
          trend: progressData.user.stats.currentStreak > 0 ? 'Active!' : 'Start today!'
        },
        {
          title: 'Level',
          value: currentLevel.toString(),
          change: `${xpNeededForNextLevel} XP to next level`,
          icon: TrendingUp,
          color: 'text-blue-600',
          bgColor: 'bg-blue-100',
          trend: `${Math.round(xpProgress)}% progress`,
          progress: xpProgress
        }
      ]);
      
    } catch (error) {
      console.error('Error loading dashboard data:', error);
      toast.error('Failed to load dashboard data');
      
      // Set fallback stats in case of error
      setStats([
        {
          title: 'Quizzes Completed',
          value: '0',
          change: 'Start your first quiz!',
          icon: Target,
          color: 'text-primary-600',
          bgColor: 'bg-primary-100'
        },
        {
          title: 'Total Points',
          value: '0',
          change: 'Earn points by completing quizzes',
          icon: Star,
          color: 'text-yellow-600',
          bgColor: 'bg-yellow-100'
        },
        {
          title: 'Current Streak',
          value: '0 days',
          change: 'Complete quizzes daily!',
          icon: Zap,
          color: 'text-green-600',
          bgColor: 'bg-green-100'
        },
        {
          title: 'Level',
          value: '1',
          change: '1000 XP to level up',
          icon: TrendingUp,
          color: 'text-blue-600',
          bgColor: 'bg-blue-100'
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const setupSocketListeners = () => {
    // Connect to socket
    socketService.connect();
    
    // Join user room
    if (user?.id) {
      socketService.joinUserRoom(user.id);
    }
    
    // Join leaderboard room
    socketService.joinLeaderboard();
    
    // Listen for notifications
    socketService.onQuizCompleted((data) => {
      toast.success(`Quiz completed! Score: ${data.score}%`);
      loadDashboardData(); // Refresh data
    });
    
    socketService.onAchievementUnlocked((data) => {
      toast.success(`ðŸŽ‰ Achievement unlocked: ${data.achievement.name}!`);
      loadDashboardData(); // Refresh data
    });
    
    socketService.onLevelUp((data) => {
      toast.success(`ðŸŽ‰ Level up! You're now level ${data.newLevel}!`);
      loadDashboardData(); // Refresh data
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="animate-fade-in bg-gradient-to-r from-primary-600 to-secondary-600 rounded-2xl p-8 text-white shadow-lg">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold mb-2">
              Welcome back, {user?.firstName || 'Learner'}! ðŸŽ¯
            </h1>
            <p className="text-primary-100 text-lg">
              Ready to continue your learning journey? You're doing amazing!
            </p>
          </div>
          <div className="flex items-center space-x-4">
            <button
              onClick={loadDashboardData}
              disabled={loading}
              className="bg-white bg-opacity-20 hover:bg-opacity-30 disabled:opacity-50 text-white p-2 rounded-lg transition-all duration-200"
              title="Refresh Dashboard"
            >
              <svg 
                className={`w-5 h-5 ${loading ? 'animate-spin' : ''}`} 
                fill="none" 
                stroke="currentColor" 
                viewBox="0 0 24 24"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 4v5h.582m15.356 2A8.001 8.001 0 004.582 9m0 0H9m11 11v-5h-.581m0 0a8.003 8.003 0 01-15.357-2m15.357 2H15" />
              </svg>
            </button>
            <div className="hidden md:block">
              <div className="w-20 h-20 bg-white bg-opacity-20 rounded-full flex items-center justify-center">
                <Brain className="w-10 h-10 text-white" />
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => (
          <div className="animate-fade-in card hover:shadow-xl transition-all duration-300 group" key={stat.title}>
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className={`${stat.bgColor} p-3 rounded-xl group-hover:scale-110 transition-transform duration-200`}>
                  <stat.icon className={`w-6 h-6 ${stat.color}`} />
                </div>
                {stat.trend && (
                  <span className={`text-xs font-medium px-2 py-1 rounded-full ${
                    stat.title === 'Current Streak' 
                      ? 'bg-green-100 text-green-700'
                      : 'bg-blue-100 text-blue-700'
                  }`}>
                    {stat.trend}
                  </span>
                )}
              </div>
              
              <div className="space-y-2">
                <p className="text-sm font-medium text-gray-600">{stat.title}</p>
                <p className="text-3xl font-bold text-gray-900">{stat.value}</p>
                <p className="text-sm text-gray-500">{stat.change}</p>
                
                {/* Progress bar for Level card */}
                {stat.title === 'Level' && stat.progress !== undefined && (
                  <div className="mt-3">
                    <div className="flex justify-between text-xs text-gray-500 mb-1">
                      <span>Progress to Level {parseInt(stat.value) + 1}</span>
                      <span>{Math.round(stat.progress)}%</span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className="bg-blue-500 h-2 rounded-full transition-all duration-500"
                        style={{ width: `${stat.progress}%` }}
                      ></div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Main Content */}
        <div className="lg:col-span-2 space-y-8">
          {/* Featured Quizzes */}
          <div className="animate-fade-in card">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">Featured Quizzes</h2>
              <a
                href="/app/quizzes"
                className="text-primary-600 hover:text-primary-700 font-medium flex items-center space-x-1"
              >
                <span>View All</span>
                <ChevronRight className="w-4 h-4" />
              </a>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {quizzes.map((quiz, index) => (
                <div className="animate-fade-in" key={quiz._id}>
                  <div className="card hover:shadow-lg transition-all duration-200">
                    <div className="p-6">
                      <div className="flex items-center justify-between mb-2">
                        <span className="text-sm text-gray-500 capitalize">{quiz.category}</span>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          quiz.difficulty === 'beginner' ? 'bg-green-100 text-green-800' :
                          quiz.difficulty === 'intermediate' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {quiz.difficulty}
                        </span>
                      </div>
                      <h3 className="text-lg font-bold text-gray-900 mb-2">{quiz.title}</h3>
                      <p className="text-gray-600 mb-4 line-clamp-2">{quiz.description}</p>
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4 text-sm text-gray-500">
                          <div className="flex items-center space-x-1">
                            <Clock className="w-4 h-4" />
                            <span>{quiz.timeLimit}m</span>
                          </div>
                          <div className="flex items-center space-x-1">
                            <Brain className="w-4 h-4" />
                            <span>{quiz.questions?.length || 0} Q</span>
                          </div>
                        </div>
                        <Link
                          to={`/app/quiz/${quiz._id}`}
                          className="btn-primary text-sm px-4 py-2 flex items-center space-x-2 hover:shadow-md transition-all duration-200"
                        >
                          <Play className="w-4 h-4" />
                          <span>Start Quiz</span>
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Quick Actions */}
          <div className="animate-fade-in card">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Quick Actions</h3>
            <div className="space-y-3">
              <button
                onClick={() => console.log('Starting daily challenge...')}
                className="w-full btn-primary flex items-center justify-center space-x-2"
              >
                <Play className="w-4 h-4" />
                <span>Start Daily Challenge</span>
              </button>
              <button
                onClick={() => console.log('Joining study group...')}
                className="w-full btn-secondary flex items-center justify-center space-x-2"
              >
                <Users className="w-4 h-4" />
                <span>Join Study Group</span>
              </button>
              <button
                onClick={() => console.log('Starting practice mode...')}
                className="w-full bg-gray-100 hover:bg-gray-200 text-gray-700 font-semibold py-3 px-6 rounded-xl transition-all duration-200 flex items-center justify-center space-x-2"
              >
                <Clock className="w-4 h-4" />
                <span>Practice Mode</span>
              </button>
            </div>
          </div>

          {/* Recent Achievements */}
          <div className="animate-fade-in card">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Recent Achievements</h3>
            <div className="space-y-3">
              {achievements.length > 0 ? (
                achievements.map((achievement) => (
                  <div key={achievement._id} className="flex items-center space-x-3 p-3 bg-gradient-to-r from-warning-50 to-warning-100 rounded-lg">
                    <div className="w-10 h-10 bg-warning-500 rounded-full flex items-center justify-center">
                      <span className="text-white text-lg">{achievement.icon}</span>
                    </div>
                    <div>
                      <p className="font-semibold text-gray-900">{achievement.name}</p>
                      <p className="text-sm text-gray-600">{achievement.description}</p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-gray-500 text-center py-4">No achievements yet. Complete quizzes to unlock them!</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
